import { useState, useEffect, useCallback } from 'react';
import axios from 'axios';

const useFetch = (url, method = 'GET', body = null, headers = {}) => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Memoizing body and headers to avoid unnecessary re-renders
  const memoizedBody = useCallback(() => body, [JSON.stringify(body)]); 
  const memoizedHeaders = useCallback(() => headers, [JSON.stringify(headers)]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const response = await axios({
          method,
          url,
          data: memoizedBody(),
          headers: memoizedHeaders(),
        });
        setData(response.data);
      } catch (err) {
        setError(err.response ? err.response.data : { message: 'Network Error' });
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [url, method, memoizedBody, memoizedHeaders]); // Dependencies are now memoized

  return { data, loading, error };
};

export default useFetch;
