import React, { useState, useEffect } from 'react';
import { TextField, Button, Box } from '@mui/material';
import useFetch from '../hooks/useFetch'; // Import useFetch hook

const SignInForm = ({ onLoginSuccess, onLoginFailure }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false); // Track if submission is in progress
  const [submitClicked, setSubmitClicked] = useState(false); // Flag for submitting

  // Trigger useFetch when submitClicked is true (form is being submitted)
  const { data, loading, error: fetchError } = useFetch(
    submitClicked ? 'http://localhost:5000/api/auth/login' : null, // Only trigger fetch when submitClicked is true
    'POST',
    { username, password },
    { 'Content-Type': 'application/json' }
  );

  // This function will run when the form is submitted
  const handleSubmit = (e) => {
    e.preventDefault();
    setError(''); // Clear previous error

    // Only trigger the request if both fields are filled
    if (username && password) {
      setSubmitClicked(true); // Trigger the fetch call
      setIsSubmitting(true); // Indicate that the form is being submitted
    } else {
      setError('Please fill in both username and password.');
    }
  };

  // Handle successful login (API returns token)
  useEffect(() => {
    if (data && data.token) {
      onLoginSuccess(data.token); // Pass the token to parent component
    }
  }, [data, onLoginSuccess]);

  // Handle error from useFetch (failed login)
  useEffect(() => {
    if (fetchError) {
      setError(fetchError.message || 'An error occurred during login');
      onLoginFailure(fetchError.message || 'An error occurred during login');
    }
  }, [fetchError, onLoginFailure]);

  return (
    <form onSubmit={handleSubmit}>
      {/* Username Field */}
      <Box className="control">
        <div className="block-cube">
          <TextField
            label="Username"
            variant="outlined"
            fullWidth
            name="username"
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            InputProps={{
              style: {
                backgroundColor: 'transparent',
                color: '#fff',
                fontFamily: 'monospace',
                letterSpacing: '0.05em',
              },
            }}
            InputLabelProps={{
              style: { color: '#fff' },
            }}
          />
        </div>
      </Box>

      {/* Password Field */}
      <Box className="control">
        <div className="block-cube">
          <TextField
            label="Password"
            variant="outlined"
            fullWidth
            name="password"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            InputProps={{
              style: {
                backgroundColor: 'transparent',
                color: '#fff',
                fontFamily: 'monospace',
                letterSpacing: '0.05em',
              },
            }}
            InputLabelProps={{
              style: { color: '#fff' },
            }}
          />
        </div>
      </Box>

      {/* Submit Button */}
      <Button
        className="btn"
        type="submit"
        fullWidth
        variant="outlined"
        sx={{
          padding: '14px 16px',
          color: '#fff',
          letterSpacing: '0.1em',
          fontWeight: 'bold',
          fontFamily: 'monospace',
          fontSize: '16px',
          position: 'relative',
          overflow: 'hidden',
          borderColor: '#fff',
          '&:hover': {
            backgroundColor: 'rgba(255, 255, 255, 0.8)',
            borderColor: '#fff',
          },
        }}
        disabled={loading || isSubmitting} // Disable button while loading or submitting
      >
        {loading || isSubmitting ? 'Logging in...' : 'Log In'}
      </Button>
    </form>
  );
};

export default SignInForm;
