import React, { useState } from 'react';
import styled from 'styled-components';

// Define the colors and gradient as constants
const bgBody = "#212121";
const bgGradientLastColor = "rgba(0,212,255,1)";
const bgGradient = `linear-gradient(90deg, rgba(2,0,36,1) 0%, rgba(52,9,121,1) 37%, ${bgGradientLastColor} 94%)`;

// Styled Components
const FormWrapper = styled.div`
  width: 300px;
  padding: 64px 15px 24px;
  margin: 0 auto;
`;

const Control = styled.div`
  margin: 0 0 24px;
`;

const Input = styled.input`
  width: 100%;
  padding: 14px 16px;
  border: 0;
  background: transparent;
  color: #fff;
  font-family: monospace, serif;
  letter-spacing: 0.05em;
  font-size: 16px;
  &:hover,
  &:focus {
    outline: none;
    border: 0;
  }
`;

const Button = styled.button`
  width: 100%;
  display: block;
  padding: 14px 16px;
  background: transparent;
  outline: none;
  border: 0;
  color: #fff;
  letter-spacing: 0.1em;
  font-weight: bold;
  font-family: monospace;
  font-size: 16px;
  cursor: pointer;
  &:hover {
    background: rgba(255, 255, 255, 0.1);
  }
`;

const BlockCube = styled.div`
  position: relative;
  .bg-top, .bg-right, .bg {
    position: absolute;
    background: ${bgGradient};
    transition: all 0.2s ease-in-out;
  }
  .bg-right {
    width: 10px;
    top: -5px;
    bottom: 5px;
    left: 100%;
    transform: skew(0, -45deg);
  }
  .bg-inner {
    position: absolute;
    left: 2px;
    top: 2px;
    right: 2px;
    bottom: 2px;
    background: ${bgBody};
    transition: all 0.2s ease-in-out;
  }
`;

const Credits = styled.div`
  position: fixed;
  left: 0;
  bottom: 0;
  padding: 15px 15px;
  width: 100%;
  z-index: 111;
  a {
    opacity: 0.6;
    color: #fff;
    font-size: 11px;
    text-decoration: none;
    &:hover {
      opacity: 1;
    }
  }
`;

const SignInForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Logging in with:', { username, password });
  };

  return (
    <FormWrapper>
      <h1>Sign In</h1>
      <form onSubmit={handleSubmit}>
        <Control>
          <BlockCube className="block-input">
            <Input
              name="username"
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </BlockCube>
        </Control>
        
        <Control>
          <BlockCube className="block-input">
            <Input
              name="password"
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </BlockCube>
        </Control>

        <Button type="submit">
          Log In
        </Button>
      </form>

      <Credits>
        <a href="https://codepen.io/marko-zub/" target="_blank" rel="noopener noreferrer">
          My other codepens
        </a>
      </Credits>
    </FormWrapper>
  );
};

export default SignInForm;
