import React, { useState, useEffect } from 'react';
import { TextField, Button, Box } from '@mui/material';
import '../CSS/SignInPage.css'; // Import custom CSS
import AnimatedWrapper from '../components/AnimatedWrapper';
import Ball from '../components/Ball'; // Import Ball component
import useFetch from '../hooks/useFetch'; // Import useFetch

const SignInPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const { data, loading, error: fetchError } = useFetch(
    'http://localhost:5000/api/auth/login',
    'POST',
    { username, password },
    { 'Content-Type': 'application/json' }
  );

  // This function will run when the form is submitted
  const handleSubmit = (e) => {
    e.preventDefault();
    setError(''); // Clear previous error

    // Only trigger the request if both fields are filled
    if (username && password) {
      setError(''); // Reset any error messages
    } else {
      setError('Please fill in both username and password.');
    }
  };

  // Log any error that comes from useFetch hook
  useEffect(() => {
    if (fetchError) {
      setError(fetchError.message || 'An error occurred during login');
    }
  }, [fetchError]); // Only run when fetchError changes

  // Handle successful login (when API returns a token)
  useEffect(() => {
    if (data && data.token) {
      localStorage.setItem('authToken', data.token); // Store token in localStorage
      window.location.href = '/feed'; // Redirect to the Feed page
    }
  }, [data]); // Only run when data changes (i.e., successful login)

  return (
    <div className="wrapper">
      {/* Left side: AnimatedWrapper and Title */}
      <div className="left-side">
        <AnimatedWrapper />
        <h1 className="title">Friendify</h1>
      </div>

      {/* Right side: Sign In Form */}
      <Box className="form-wrapper">
        <h1>Sign In</h1>
        {error && <div style={{ color: 'red', marginBottom: '10px' }}>{error}</div>} {/* Show error message */}
        <form onSubmit={handleSubmit}>
          {/* Username Field */}
          <Box className="control">
            <div className="block-cube">
              <TextField
                label="Username"
                variant="outlined"
                fullWidth
                name="username"
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                InputProps={{
                  style: {
                    backgroundColor: 'transparent',
                    color: '#fff',
                    fontFamily: 'monospace',
                    letterSpacing: '0.05em',
                  },
                }}
                InputLabelProps={{
                  style: { color: '#fff' },
                }}
              />
            </div>
          </Box>

          {/* Password Field */}
          <Box className="control">
            <div className="block-cube">
              <TextField
                label="Password"
                variant="outlined"
                fullWidth
                name="password"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                InputProps={{
                  style: {
                    backgroundColor: 'transparent',
                    color: '#fff',
                    fontFamily: 'monospace',
                    letterSpacing: '0.05em',
                  },
                }}
                InputLabelProps={{
                  style: { color: '#fff' },
                }}
              />
            </div>
          </Box>

          {/* Submit Button */}
          <Button
            className="btn"
            type="submit"
            fullWidth
            variant="outlined"
            sx={{
              padding: '14px 16px',
              color: '#fff',
              letterSpacing: '0.1em',
              fontWeight: 'bold',
              fontFamily: 'monospace',
              fontSize: '16px',
              position: 'relative',
              overflow: 'hidden',
              borderColor: '#fff',
              '&:hover': {
                backgroundColor: 'rgba(255, 255, 255, 0.8)',
                borderColor: '#fff',
              },
            }}
            disabled={loading} // Disable button while loading
          >
            {loading ? 'Logging in...' : 'Log In'}
          </Button>
        </form>
      </Box>

      {/* Ball Cursor */}
      <Ball borderColor="rgba(0, 212, 255, 1)" />
    </div>
  );
};

export default SignInPage;
