// SignInPage.js
import React, { useState } from 'react';
import SignInForm from '../components/SignInForm'; // Import SignInForm
import SignUpForm from '../components/SignUpForm'; // Import SignUpForm
import AnimatedWrapper from '../components/AnimatedWrapper'; // Import animation wrapper
import Ball from '../components/Ball'; // Import Ball animation component
import '../CSS/SignInPage.css'; // Import shared CSS styles

const SignInPage = () => {
  const [isSignUp, setIsSignUp] = useState(false); // State to toggle between SignIn and SignUp

  // Handlers for successful and failed login/signup
  const handleSignupSuccess = (token) => {
    localStorage.setItem('authToken', token); // Store token in localStorage
    window.location.href = '/dashboard'; // Redirect to the dashboard or home page
  };

  const handleSignupFailure = (error) => {
    console.error(error); // Handle failure (display error, etc.)
  };

  const handleSigninSuccess = (token) => {
    localStorage.setItem('authToken', token); // Store token in localStorage
    window.location.href = '/dashboard'; // Redirect to the dashboard or home page
  };

  const handleSigninFailure = (error) => {
    console.error(error); // Handle failure (display error, etc.)
  };

  // Toggle between SignIn and SignUp forms
  const toggleForm = () => {
    setIsSignUp((prev) => !prev);
  };

  return (
    <div className="wrapper">
      {/* Left side: Animated Wrapper */}
      <div className="left-side">
        <AnimatedWrapper />
        <h1 className="title">Friendify</h1>
      </div>

      {/* Right side: Form Section */}
      <div className={`form-container ${isSignUp ? 'sign-up' : 'sign-in'}`}>
        {isSignUp ? (
          <SignUpForm onSignupSuccess={handleSignupSuccess} onSignupFailure={handleSignupFailure} />
        ) : (
          <SignInForm onLoginSuccess={handleSigninSuccess} onLoginFailure={handleSigninFailure} />
        )}
        <div className="switch-form">
          {isSignUp ? (
            <p>
              Already have an account?{' '}
              <button className="switch-btn" onClick={toggleForm}>
                Sign In
              </button>
            </p>
          ) : (
            <p>
              Don't have an account?{' '}
              <button className="switch-btn" onClick={toggleForm}>
                Sign Up
              </button>
            </p>
          )}
        </div>
      </div>

      {/* Ball Cursor */}
      <Ball borderColor="rgba(0, 212, 255, 1)" />
    </div>
  );
};

export default SignInPage;
