// SignInPage.js
import React, { useState, useCallback } from 'react';
import { Box } from '@mui/material';
import '../CSS/SignInPage.css'; // Import custom CSS
import AnimatedWrapper from '../components/AnimatedWrapper';
import Ball from '../components/Ball'; // Import Ball component
import SignInForm from '../components/SignInForm'; // Import SignInForm

const SignInPage = () => {
  const [error, setError] = useState('');

  // Use useCallback to ensure the functions are not recreated on every render
  const handleLoginSuccess = useCallback((token) => {
    localStorage.setItem('authToken', token);
    window.location.href = '/feed'; // Redirect to the Feed page
  }, []);

  const handleLoginFailure = useCallback((errorMessage) => {
    setError(errorMessage); // Update error state on failure
  }, []);

  return (
    <div className="wrapper">
      {/* Left side: AnimatedWrapper and Title */}
      <div className="left-side">
        <AnimatedWrapper />
        <h1 className="title">Friendify</h1>
      </div>

      {/* Right side: Sign In Form */}
      <Box className="form-wrapper">
        {error && <div style={{ color: 'red', marginBottom: '10px' }}>{error}</div>}
        <SignInForm onLoginSuccess={handleLoginSuccess} onLoginFailure={handleLoginFailure} />
      </Box>

      {/* Ball Cursor */}
      <Ball borderColor="rgba(0, 212, 255, 1)" />
    </div>
  );
};

export default SignInPage;
