import React, { useState, useEffect, useRef } from 'react';
import { TextField, Button, Box } from '@mui/material';
import useFetch from '../hooks/useFetch'; // Import useFetch

const SignInForm = ({ onLoginSuccess, onLoginFailure }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [submitClicked, setSubmitClicked] = useState(false);
  const formSubmitted = useRef(false); // This will prevent double submission

  // This function will run when the form is submitted
  const handleSubmit = (e) => {
    e.preventDefault();
    setError(''); // Clear previous error

    if (username && password) {
      if (formSubmitted.current) return; // Prevent multiple submissions
      formSubmitted.current = true; // Mark form as submitted
      setSubmitClicked(true); // Trigger fetch
    } else {
      setError('Please fill in both username and password.');
    }
  };

  // Fetch the login data only after form submission
  const { data, loading, error: fetchError } = useFetch(
    submitClicked ? 'http://localhost:5000/api/auth/login' : null, // Only trigger the fetch if submitClicked is true
    'POST',
    { username, password },
    { 'Content-Type': 'application/json' }
  );

  useEffect(() => {
    if (fetchError) {
      setError(fetchError.message || 'An error occurred during login');
      onLoginFailure(fetchError.message || 'An error occurred during login');
      formSubmitted.current = false; // Reset for next submission
    }
  }, [fetchError, onLoginFailure]);

  useEffect(() => {
    if (data && data.token) {
      localStorage.setItem('authToken', data.token); // Store token in localStorage
      onLoginSuccess(data.token); // Notify parent component of success
    }
  }, [data, onLoginSuccess]);

  return (
    <Box className="form-wrapper">
      <h1>Sign In</h1>
      {error && <div style={{ color: 'red', marginBottom: '10px' }}>{error}</div>} {/* Show error message */}
      <form onSubmit={handleSubmit}>
        {/* Username Field */}
        <Box className="control">
          <div className="block-cube">
            <TextField
              label="Username"
              variant="outlined"
              fullWidth
              name="username"
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              InputProps={{
                style: {
                  backgroundColor: 'transparent',
                  color: '#fff',
                  fontFamily: 'monospace',
                  letterSpacing: '0.05em',
                },
              }}
              InputLabelProps={{
                style: { color: '#fff' },
              }}
            />
          </div>
        </Box>

        {/* Password Field */}
        <Box className="control">
          <div className="block-cube">
            <TextField
              label="Password"
              variant="outlined"
              fullWidth
              name="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              InputProps={{
                style: {
                  backgroundColor: 'transparent',
                  color: '#fff',
                  fontFamily: 'monospace',
                  letterSpacing: '0.05em',
                },
              }}
              InputLabelProps={{
                style: { color: '#fff' },
              }}
            />
          </div>
        </Box>

        {/* Submit Button */}
        <Button
          className="btn"
          type="submit"
          fullWidth
          variant="outlined"
          sx={{
            padding: '14px 16px',
            color: '#fff',
            letterSpacing: '0.1em',
            fontWeight: 'bold',
            fontFamily: 'monospace',
            fontSize: '16px',
            position: 'relative',
            overflow: 'hidden',
            borderColor: '#fff',
            '&:hover': {
              backgroundColor: 'rgba(255, 255, 255, 0.8)',
              borderColor: '#fff',
            },
          }}
          disabled={loading} // Disable button while loading
        >
          {loading ? 'Logging in...' : 'Log In'}
        </Button>
      </form>
    </Box>
  );
};

export default SignInForm;
