import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";

// Initial order of colors to animate
const initialOrder = [
  "#ffb3d9", // Pastel pink
  "#b3d9ff", // Pastel blue
  "#ffb3ff", // Pastel purple
  "#d0d8ff", // Soft lavender
];

export default function AnimatedWrapper() {
  const [order, setOrder] = useState(initialOrder);

  // Shuffle the order array on every render
  useEffect(() => {
    setOrder(shuffle([...order])); // Create a new shuffled copy of the order array
  }, [order]); // Dependency array with `order` will shuffle on every render

  return (
    <ul style={container}>
      {order.map((backgroundColor, index) => (
        <motion.li
          key={index} // Use index to ensure a unique key for each element
          layout
          transition={spring} // Apply the transition animation
          style={{ ...item, backgroundColor }}
        />
      ))}
    </ul>
  );
}

// Shuffle function to randomize the order
function shuffle(array) {
  return array.sort(() => Math.random() - 0.5);
}

// Transition settings for animation
const spring = {
  type: "spring",
  damping: 20,
  stiffness: 300,
};

// Styles for the container and list items
const container = {
  listStyle: "none",
  padding: 0,
  margin: 0,
  position: "relative",
  display: "flex",
  flexWrap: "wrap",
  gap: 10,
  width: 300,
  flexDirection: "row",
  justifyContent: "center",
  alignItems: "center",
};

const item = {
  width: 100,
  height: 100,
  borderRadius: "10px",
};
