import React, { useState } from 'react';
import { TextField, Button, Box } from '@mui/material';
import '../CSS/SignInPage.css'; // Import custom CSS
import AnimatedWrapper from '../components/AnimatedWrapper';
import Ball from '../components/Ball'; // Import Ball component
import { useNavigate } from 'react-router-dom'; // Import useNavigate for routing after login

const SignInPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate(); // Hook for navigation after successful login

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Logging in with:', { username, password });

    try {
      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();
      
      if (response.ok) {
        // Save token in localStorage (or cookies)
        localStorage.setItem('authToken', data.token);
        navigate('/feed'); // Redirect to feed page after successful login
      } else {
        setError(data.message); // Show error message if login fails
      }
    } catch (err) {
      console.error('Error logging in:', err);
      setError('An error occurred. Please try again.');
    }
  };

  return (
    <div className="wrapper">
      <div className="left-side">
        <AnimatedWrapper />
        <h1 className="title">Friendify</h1>
      </div>

      <Box className="form-wrapper">
        <h1>Sign In</h1>
        <form onSubmit={handleSubmit}>
          {/* Username Field */}
          <Box className="control">
            <div className="block-cube">
              <TextField
                label="Username"
                variant="outlined"
                fullWidth
                name="username"
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                InputProps={{
                  style: {
                    backgroundColor: 'transparent',
                    color: '#fff',
                    fontFamily: 'monospace',
                    letterSpacing: '0.05em',
                  },
                }}
                InputLabelProps={{
                  style: { color: '#fff' },
                }}
              />
            </div>
          </Box>

          {/* Password Field */}
          <Box className="control">
            <div className="block-cube">
              <TextField
                label="Password"
                variant="outlined"
                fullWidth
                name="password"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                InputProps={{
                  style: {
                    backgroundColor: 'transparent',
                    color: '#fff',
                    fontFamily: 'monospace',
                    letterSpacing: '0.05em',
                  },
                }}
                InputLabelProps={{
                  style: { color: '#fff' },
                }}
              />
            </div>
          </Box>

          {/* Submit Button */}
          <Button
            className="btn"
            type="submit"
            fullWidth
            variant="outlined"
            sx={{
              padding: '14px 16px',
              color: '#fff',
              letterSpacing: '0.1em',
              fontWeight: 'bold',
              fontFamily: 'monospace',
              fontSize: '16px',
              position: 'relative',
              overflow: 'hidden',
              borderColor: '#fff',
              '&:hover': {
                backgroundColor: 'rgba(255, 255, 255, 0.8)',
                borderColor: '#fff',
              },
            }}
          >
            Log In
          </Button>
        </form>
        {error && <div style={{ color: 'red', marginTop: '10px' }}>{error}</div>}
      </Box>

      <Ball borderColor="rgba(0, 212, 255, 1)" />
    </div>
  );
};

export default SignInPage;
