import React, { useState } from 'react';
import { TextField, Button, Box } from '@mui/material';
import '../CSS/SignInPage.css'; // Import custom CSS
import AnimatedWrapper from '../components/AnimatedWrapper';
import Ball from '../components/Ball'; // Import Ball component

const SignInPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Logging in with:', { username, password });
  };

  return (
    <div className="wrapper">
      {/* Left side: AnimatedWrapper */}
      <div className="animated-wrapper">
        <AnimatedWrapper />
        <h1 className="friendify-title">Friendify</h1> {/* Faded Title */}
      </div>

      {/* Right side: Sign In Form */}
      <Box className="form-wrapper">
        <h1>Sign In</h1>
        <form onSubmit={handleSubmit}>
          {/* Username Field */}
          <Box className="control">
            <div className="block-cube">
              <TextField
                label="Username"
                variant="outlined"
                fullWidth
                name="username"
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                InputProps={{
                  style: {
                    backgroundColor: 'transparent',
                    color: '#fff',
                    fontFamily: 'monospace',
                    letterSpacing: '0.05em',
                    border: '2px solid transparent',
                    borderImage: 'linear-gradient(45deg, #ffb3d9, #b3d9ff) 1',
                  },
                }}
                InputLabelProps={{
                  style: { color: '#fff' },
                }}
              />
            </div>
          </Box>

          {/* Password Field */}
          <Box className="control">
            <div className="block-cube">
              <TextField
                label="Password"
                variant="outlined"
                fullWidth
                name="password"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                InputProps={{
                  style: {
                    backgroundColor: 'transparent',
                    color: '#fff',
                    fontFamily: 'monospace',
                    letterSpacing: '0.05em',
                    border: '2px solid transparent',
                    borderImage: 'linear-gradient(45deg, #ffb3d9, #b3d9ff) 1',
                  },
                }}
                InputLabelProps={{
                  style: { color: '#fff' },
                }}
              />
            </div>
          </Box>

          {/* Submit Button */}
          <Button
            className="btn"
            type="submit"
            fullWidth
            variant="outlined"
            sx={{
              padding: '14px 16px',
              color: '#fff',
              letterSpacing: '0.1em',
              fontWeight: 'bold',
              fontFamily: 'monospace',
              fontSize: '16px',
              position: 'relative',
              overflow: 'hidden',
              borderColor: 'transparent',
              borderImage: 'linear-gradient(45deg, #ffb3d9, #b3d9ff) 1',
              '&:hover': {
                backgroundColor: 'rgba(255, 255, 255, 0.8)',
                borderColor: 'transparent',
              },
            }}
          >
            Log In
          </Button>
        </form>
      </Box>

      {/* Ball Cursor */}
      <Ball borderColor="rgba(0, 212, 255, 1)" />
    </div>
  );
};

export default SignInPage;
