import React, { useState } from 'react';
import { Box } from '@mui/material';
import AnimatedWrapper from '../components/AnimatedWrapper';
import Ball from '../components/Ball'; // Import Ball component
import SignInForm from '../components/SignInForm'; // Import the SignInForm component

const SignInPage = () => {
  const [error, setError] = useState('');
  const [token, setToken] = useState(null);

  // Handle successful login (store token and redirect)
  const handleLoginSuccess = (token) => {
    setToken(token);
    localStorage.setItem('authToken', token);
    window.location.href = '/feed'; // Redirect to feed page
  };

  // Handle failed login (show error)
  const handleLoginFailure = (errorMessage) => {
    setError(errorMessage);
  };

  return (
    <div className="wrapper">
      {/* Left side: AnimatedWrapper and Title */}
      <div className="left-side">
        <AnimatedWrapper />
        <h1 className="title">Friendify</h1>
      </div>

      {/* Right side: Sign In Form */}
      <Box className="form-wrapper">
        <h1>Sign In</h1>
        {error && <div style={{ color: 'red', marginBottom: '10px' }}>{error}</div>} {/* Show error message */}
        <SignInForm
          onLoginSuccess={handleLoginSuccess}
          onLoginFailure={handleLoginFailure}
        />
      </Box>

      {/* Ball Cursor */}
      <Ball borderColor="rgba(0, 212, 255, 1)" />
    </div>
  );
};

export default SignInPage;
