import { useEffect, useRef } from "react";
import { motion, useMotionValue, useSpring } from "framer-motion";

const spring = { damping: 20, stiffness: 300, restDelta: 0.001 };

export function Ball({ borderColor }) {
  const ref = useRef(null);
  const { x, y } = useFollowPointer(ref);

  return (
    <motion.div
      ref={ref}
      className="follow-pointer-ball"
      style={{
        x,
        y,
        borderColor: borderColor, // Apply the color to the border
      }}
    />
  );
}

function useFollowPointer(ref) {
  const xPoint = useMotionValue(0);
  const yPoint = useMotionValue(0);
  const x = useSpring(xPoint, spring);
  const y = useSpring(yPoint, spring);

  useEffect(() => {
    if (!ref.current) return;

    const handlePointerMove = ({ clientX, clientY }) => {
      const element = ref.current;

      xPoint.set(
        clientX - element.offsetLeft - element.offsetWidth / 2
      );
      yPoint.set(
        clientY - element.offsetTop - element.offsetHeight / 2
      );
    };

    window.addEventListener("pointermove", handlePointerMove);

    return () => {
      window.removeEventListener("pointermove", handlePointerMove);
    };
  }, []);

  return { x, y };
}
