import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';

// Reordering component for animated list items
export default function Reordering() {
  const [order, setOrder] = useState(initialOrder);

  // Shuffle the order every second to trigger the animation
  useEffect(() => {
    const timeout = setTimeout(() => setOrder(shuffle(order)), 1000);
    return () => clearTimeout(timeout);
  }, [order]);

  return (
    <ul style={container}>
      {order.map((backgroundColor) => (
        <motion.li
          key={backgroundColor}
          layout // Make sure layout is applied for animation
          transition={spring} // Apply the transition animation
          style={{ ...item, backgroundColor }}
        />
      ))}
    </ul>
  );
}

// Initial order of colors to animate
const initialOrder = [
  "#ff0088",
  "#dd00ee",
  "#9911ff",
  "#0d63f8",
];

// Shuffle function to randomize the order
function shuffle(array) {
  return array.sort(() => Math.random() - 0.5);
}

// Transition settings for animation
const spring = {
  type: "spring",
  damping: 20,
  stiffness: 300,
};

// Styles for the container and list items
const container = {
  listStyle: "none",
  padding: 0,
  margin: 0,
  position: "relative",
  display: "flex",
  flexWrap: "wrap",
  gap: 10,
  width: 300,
  flexDirection: "row",
  justifyContent: "center",
  alignItems: "center",
};

const item = {
  width: 100,
  height: 100,
  borderRadius: "10px",
};
